import {Link, useNavigate} from 'react-router-dom';
import React from 'react';
import axios from 'axios'
import { useState } from 'react';
import { UilUser,UilAt,UilLock} from '@iconscout/react-unicons'


const Login=()=>{
    const [data, setData]=useState({
        email:"",
        password:"",
    })

    const[error, setError]=useState("");

    const navigate=useNavigate();

    const handleChange=({currentTarget:input})=>{
        setData({...data,[input.name]:input.value});
    }
    const handleSubmit=async(e)=>{
        e.preventDefault();
        try{
            const url="http://localhost:5000/api/auth";
            const{data:res}=await axios.post(url, data);
            localStorage.setItem("token", res.data);
            window.location="/"
            
        }catch(error){
            if(error.response && error.response.status>=400 && error.response.status<=500)
            {
                setError(error.response.data.message)
            }
        }
    }

    return(
        <div class="min-h-screen bg-emerald-500 flex justify-center items-center ">
        <div class="absolute w-60 h-60 rounded-xl bg-emerald-300 -top-5 -left-16 z-0 transform rotate-45 hidden md:block">
        </div>
        <div class="absolute w-48 h-48 rounded-xl bg-emerald-300 -bottom-5 -right-10 z-0 transform rotate-12 hidden md:block">
        </div>
        <div class=" py-7 px-4 h-800 min-h-800 sm:py-12 sm:px-12  bg-white rounded-2xl shadow-xl z-20">
            <div>
                <h1 class="text-3xl font-bold text-center mb-4 cursor-pointer">Login</h1>
                <p class="w-auto sm:w-80 text-center text-sm mb-8 font-semibold text-gray-700 tracking-wide cursor-pointer">Create an
                    account to enjoy all the services without any ads for free!</p>
            </div>
            <div class="space-y-4">
            <div class="flex flex-row"  >
            <UilAt size={30} class="text-emerald-600"/>
            <input type="email" placeholder="email" name='email' value={data.email} onChange={handleChange} class="block text-sm py-2 px-4 rounded-lg w-full border outline-none" />
        </div>
        
        <div class="flex flex-row" >
            <UilLock size={30} class="text-emerald-600 "/>
            <input type="password" placeholder="Password" name='password' value={data.password} onChange={handleChange} class="block text-sm py-2 px-4 rounded-lg w-full border outline-none" />
        </div>
            </div>
                {error &&<p class="mt-4 text-sm">{error}</p>}
                <div class="text-center mt-6">
                    <button type="submit" onClick={handleSubmit} class="py-3 w-64 text-xl text-white bg-emerald-500 rounded-2xl">login</button>
                    <p class="mt-4 text-sm">Dont have an Account? 
                        <span class="underline cursor-pointer">
                            <Link to="/Signup">
                                <button type='button' >
                                    Sign up!
                                </button>
                            </Link>
                        </span>
                    </p>
                </div>
            </div>
            <div class="w-40 h-40 absolute bg-emerald-200 rounded-full top-0 right-12 hidden md:block"></div>
            <div
                class="w-20 h-40 absolute bg-emerald-200 rounded-full bottom-20 left-10 transform rotate-45 hidden md:block">
            </div>
        </div>
    )
}

export default Login;
